					<Suspense fallback={<span>Loading...</span>}>
						<B />
					</Suspense>
					<Suspense fallback={<span>Loading...</span>}>
						<C />
					</Suspense>
				</SuspenseList>
				<Suspense fallback={<span>Loading...</span>}>
					<D />
				</Suspense>
			</SuspenseList>,
			scratch
		);
		return [A.resolve, B.resolve, C.resolve, D.resolve];
