@keyframes popup {
	from {
		box-shadow: 0 0 0 black;
		opacity: 0;
		transform: scale(0.9);
	}
	to {
		box-shadow: 0 30px 70px rgba(0, 0, 0, 0.5);
		opacity: 1;
		transform: none;
	}
}

@keyframes zoom {
	from {
		opacity: 0;
		transform: scale(0.8);
	}
	to {
		opacity: 1;
		transform: none;
	}
}

@keyframes appear-from-left {
	from {
		opacity: 0;
		transform: translateX(-25px);
	}
	to {
		opacity: 1;
		transform: none;
	}
}

#people-app {
	position: relative;
	overflow: hidden;
	min-height: 100vh;
	animation: popup 300ms cubic-bezier(0.3, 0.7, 0.3, 1) forwards;
	background: var(--app-background);
	--menu-width: 260px;
	--menu-item-height: 50px;

	@media (min-width: 1280px) {
		max-width: 1280px;
		min-height: calc(100vh - 64px);
		margin: 32px auto;
		border-radius: 10px;
	}

	> nav {
		position: absolute;
		display: flow-root;
		width: var(--menu-width);
		height: 100%;
		background-color: var(--app-background-secondary);
		overflow-x: hidden;
		overflow-y: auto;
	}

	> nav h4 {
		padding-left: 16px;
		font-weight: normal;
		text-transform: uppercase;
	}

	> nav ul {
		position: relative;
	}

	> nav li {
		position: absolute;
		width: 100%;
		animation: zoom 200ms forwards;
		opacity: 0;
		transition: top 200ms;
	}

	> nav li > a {
		position: relative;
		display: flex;
		overflow: hidden;
		flex-flow: row;
		align-items: center;
		margin-left: 16px;
		border-right: 2px solid transparent;
		border-bottom-left-radius: 48px;
		border-top-left-radius: 48px;
		text-transform: capitalize;
		transition: border 500ms;
	}

	> nav li > a:hover {
		background-color: var(--app-highlight);
	}

	> nav li > a::after {
		position: absolute;
		top: 0;
		right: -2px;
		bottom: 0;
		left: 0;
		background-image: radial-gradient(
			circle,
			var(--app-ripple) 1%,
			transparent 1%
		);
		background-position: center;
		background-repeat: no-repeat;
		background-size: 10000%;
		content: '';
		opacity: 0;
		transition: opacity 700ms, background 300ms;
	}

	> nav li > a:active::after {
		background-size: 100%;
		opacity: 0.5;
		transition: none;
	}

	> nav li > a.active {
		border-color: var(--app-primary);
		background-color: var(--app-highlight);
	}

	> nav li > a > * {
		margin: 8px;
	}

	#people-main {
		padding-left: var(--menu-width);
	}
}

#people-app {
	.avatar {
		display: inline-block;
		overflow: hidden;
		width: var(--avatar-size, 32px);
		height: var(--avatar-size, 32px);
		background-color: var(--avatar-color, var(--app-primary));
		border-radius: 50%;
		font-size: calc(var(--avatar-size, 32px) * 0.5);
		line-height: var(--avatar-size, 32px);
		object-fit: cover;
		text-align: center;
		text-transform: uppercase;
		white-space: nowrap;
	}
}

#people-app {
	button {
		position: relative;
		overflow: hidden;
		min-width: 36px;
		height: 36px;
		padding: 0 16px;
		border: none;
		background-color: transparent;
		border-radius: 4px;
		color: var(--app-text);
		font-family: 'Montserrat', sans-serif;
		font-size: 14px;
		font-weight: 600;
		letter-spacing: 0.08em;
		text-transform: uppercase;
		transition: background 300ms, color 200ms;
		white-space: nowrap;
	}

	button::before {
		position: absolute;
		top: 0;
		right: 0;
		bottom: 0;
		left: 0;
		background-color: var(--app-ripple);
		content: '';
		opacity: 0;
		transition: opacity 200ms;
	}

	button:hover:not(:disabled)::before {
		opacity: 0.3;
		transition: opacity 100ms;
	}

	button:active:not(:disabled)::before {
		opacity: 0.7;
		transition: none;
	}

	button::after {
		position: absolute;
		top: 0;
		right: 0;
		bottom: 0;
		left: 0;
		background-image: radial-gradient(
			circle,
			var(--app-ripple) 1%,
			transparent 1%
		);
		background-position: center;
		background-repeat: no-repeat;
		background-size: 20000%;
		content: '';
		opacity: 0;
		transition: opacity 700ms, background 400ms;
	}

	button:active:not(:disabled)::after {
		background-size: 100%;
		opacity: 1;
		transition: none;
	}

	button.primary {
		background-color: var(--app-primary);
		box-shadow: 0 2px 6px var(--app-shadow);
	}

	button.secondary {
		background-color: var(--app-secondary);
		box-shadow: 0 2px 6px var(--app-shadow);
	}

	button:disabled {
		color: var(--app-text-secondary);
	}

	button.busy {
		animation: stripes 500ms linear infinite;
		background-image: repeating-linear-gradient(
			45deg,
			var(--app-shadow) 0%,
			var(--app-shadow) 25%,
			transparent 25%,
			transparent 50%,
			var(--app-shadow) 50%,
			var(--app-shadow) 75%,
			transparent 75%,
			transparent 100%
		);
		color: var(--app-text);
		/* letter-spacing: -.7em; */
	}

	button:disabled:not(.primary):not(.secondary).busy,
	button:disabled.primary:not(.busy),
	button:disabled.secondary:not(.busy) {
		background-color: var(--app-background-disabled);
	}

	@keyframes stripes {
		from {
			background-position-x: 0;
			background-size: 16px 16px;
		}
		to {
			background-position-x: 16px;
			background-size: 16px 16px;
		}
	}
}

