	rotateBackward = () => {
		let { items, count } = this.state,
			len = items.length;
		items = items.slice(len - count, len).concat(items.slice(0, len - count));
		this.setState({ items });
	};

	toggleKeys = () => {
		this.setState({ useKeys: !this.state.useKeys });
	};

	renderItem = item => (
		<li key={this.state.useKeys ? item.key : null}>{item.label}</li>
