			render(props, state) {
				if (errored) {
					return <p>Error</p>;
				}

				return <Fragment>{props.page === 1 ? <Page1 /> : <Page2 />}</Fragment>;
			}
		}

		act(() => render(<App page={2} />, scratch));
		expect(scratch.innerHTML).to.equal('<p>Load</p>');

		act(() => render(<App page={1} />, scratch));
		expect(spy).to.be.calledOnce;
		expect(scratch.innerHTML).to.equal('<p>Error</p>');
	});

	it('orders effects effectively', () => {
		const calls = [];
		const GrandChild = ({ id }) => {
			useLayoutEffect(() => {
				calls.push(`${id} - Effect`);
				return () => {
					calls.push(`${id} - Cleanup`);
				};
			}, [id]);
			return <p>{id}</p>;
		};

		const Child = ({ id }) => {
			useLayoutEffect(() => {
				calls.push(`${id} - Effect`);
				return () => {
					calls.push(`${id} - Cleanup`);
				};
			}, [id]);
			return (
				<Fragment>
					<GrandChild id={`${id}-GrandChild-1`} />
					<GrandChild id={`${id}-GrandChild-2`} />
				</Fragment>
			);
		};

		function Parent() {
			useLayoutEffect(() => {
				calls.push('Parent - Effect');
				return () => {
					calls.push('Parent - Cleanup');
				};
			}, []);
			return (
				<div className="App">
					<Child id="Child-1" />
					<div>
						<Child id="Child-2" />
					</div>
					<Child id="Child-3" />
				</div>
			);
		}

		act(() => {
			render(<Parent />, scratch);
		});

		expect(calls).to.deep.equal([
			'Child-1-GrandChild-1 - Effect',
			'Child-1-GrandChild-2 - Effect',
			'Child-1 - Effect',
			'Child-2-GrandChild-1 - Effect',
			'Child-2-GrandChild-2 - Effect',
			'Child-2 - Effect',
			'Child-3-GrandChild-1 - Effect',
			'Child-3-GrandChild-2 - Effect',
			'Child-3 - Effect',
			'Parent - Effect'
		]);
	});

	it('should cancel effects from a disposed render', () => {
		const calls = [];
		const App = () => {
			const [greeting, setGreeting] = useState('bye');

			useLayoutEffect(() => {
				calls.push('doing effect' + greeting);
				return () => {
					calls.push('cleaning up' + greeting);
				};
			}, [greeting]);

			if (greeting === 'bye') {
				setGreeting('hi');
			}

			return <p>{greeting}</p>;
		};

		act(() => {
			render(<App />, scratch);
		});
		expect(calls.length).to.equal(1);
		expect(calls).to.deep.equal(['doing effecthi']);
	});

	it('should not rerun committed effects', () => {
		const calls = [];
		const App = ({ i }) => {
			const [greeting, setGreeting] = useState('hi');

			useLayoutEffect(() => {
				calls.push('doing effect' + greeting);
				return () => {
					calls.push('cleaning up' + greeting);
				};
			}, []);

			if (i === 2) {
				setGreeting('bye');
			}

			return <p>{greeting}</p>;
		};

		act(() => {
			render(<App />, scratch);
		});
		expect(calls.length).to.equal(1);
		expect(calls).to.deep.equal(['doing effecthi']);

		act(() => {
			render(<App i={2} />, scratch);
		});
	});

	it('should not schedule effects that have no change', () => {
		const calls = [];
		let set;
		const App = ({ i }) => {
			const [greeting, setGreeting] = useState('hi');
			set = setGreeting;

			useLayoutEffect(() => {
				calls.push('doing effect' + greeting);
				return () => {
					calls.push('cleaning up' + greeting);
				};
			}, [greeting]);

			if (greeting === 'bye') {
				setGreeting('hi');
			}

			return <p>{greeting}</p>;
		};

		act(() => {
			render(<App />, scratch);
		});
		expect(calls.length).to.equal(1);
		expect(calls).to.deep.equal(['doing effecthi']);

		act(() => {
			set('bye');
		});
		expect(calls.length).to.equal(1);
		expect(calls).to.deep.equal(['doing effecthi']);
	});

	it('should run layout affects after all refs are invoked', () => {
		const calls = [];
		const verifyRef = name => el => {
			calls.push(name);
			expect(document.body.contains(el), name).to.equal(true);
		};

		const App = () => {
			const ref = useRef();
			useLayoutEffect(() => {
				expect(ref.current).to.equalNode(scratch.querySelector('p'));

				calls.push('doing effect');
				return () => {
					calls.push('cleaning up');
				};
			});

			return (
				<div ref={verifyRef('callback ref outer')}>
					<p ref={ref}>
						<span ref={verifyRef('callback ref inner')}>Hi</span>
					</p>
				</div>
			);
		};

		act(() => {
			render(<App />, scratch);
		});
		expect(calls).to.deep.equal([
			'callback ref inner',
			'callback ref outer',
			'doing effect'
		]);
	});
});

import { createElement, render } from 'preact';
import { setupScratch, teardown } from '../../../test/_util/helpers';
import { useMemo, useState } from 'preact/hooks';
import { act } from 'preact/test-utils';

/** @jsx createElement */

describe('useMemo', () => {
	/** @type {HTMLDivElement} */
	let scratch;

	beforeEach(() => {
		scratch = setupScratch();
	});

	afterEach(() => {
		teardown(scratch);
	});

	it('only recomputes the result when inputs change', () => {
		let memoFunction = sinon.spy((a, b) => a + b);
		const results = [];

		function Comp({ a, b }) {
			const result = useMemo(() => memoFunction(a, b), [a, b]);
			results.push(result);
			return null;
		}

		render(<Comp a={1} b={1} />, scratch);
		render(<Comp a={1} b={1} />, scratch);

		expect(results).to.deep.equal([2, 2]);
		expect(memoFunction).to.have.been.calledOnce;

		render(<Comp a={1} b={2} />, scratch);
		render(<Comp a={1} b={2} />, scratch);

		expect(results).to.deep.equal([2, 2, 3, 3]);
		expect(memoFunction).to.have.been.calledTwice;
	});

	it('should rerun when deps length changes', () => {
		let memoFunction = sinon.spy(() => 1 + 2);

		function Comp({ all }) {
			const deps = [1, all && 2].filter(Boolean);
			const result = useMemo(() => memoFunction(), deps);
			return result;
		}

		render(<Comp all />, scratch);
		expect(memoFunction).to.have.been.calledOnce;
		render(<Comp all={false} />, scratch);
		expect(memoFunction).to.have.been.calledTwice;
	});

	it('should rerun when first run threw an error', () => {
		let hasThrown = false;
		let memoFunction = sinon.spy(() => {
			if (!hasThrown) {
				hasThrown = true;
				throw new Error('test');
			} else {
				return 3;
			}
		});

		function Comp() {
			const result = useMemo(() => memoFunction(), []);
			return result;
		}

		expect(() => render(<Comp />, scratch)).to.throw('test');
		expect(memoFunction).to.have.been.calledOnce;
		expect(() => render(<Comp />, scratch)).not.to.throw();
		expect(memoFunction).to.have.been.calledTwice;
	});

	it('short circuits diffing for memoized components', () => {
		let spy = sinon.spy();
		let spy2 = sinon.spy();
		const X = ({ count }) => {
			spy();
			return <span>{count}</span>;
		};

		const Y = ({ count }) => {
			spy2();
			return <p>{count}</p>;
		};

		const App = ({ x }) => {
			const y = useMemo(() => <Y count={x} />, [x]);
			return (
				<div>
					<X count={x} />
					{y}
				</div>
			);
		};

		render(<App x={0} />, scratch);
		expect(spy).to.be.calledOnce;
		expect(spy2).to.be.calledOnce;
		expect(scratch.innerHTML).to.equal('<div><span>0</span><p>0</p></div>');

		render(<App x={0} />, scratch);
		expect(spy).to.be.calledTwice;
		expect(spy2).to.be.calledOnce;
		expect(scratch.innerHTML).to.equal('<div><span>0</span><p>0</p></div>');

		render(<App x={1} />, scratch);
		expect(spy).to.be.calledThrice;
		expect(spy2).to.be.calledTwice;
		expect(scratch.innerHTML).to.equal('<div><span>1</span><p>1</p></div>');

		render(<App x={1} />, scratch);
		expect(spy2).to.be.calledTwice;
		expect(scratch.innerHTML).to.equal('<div><span>1</span><p>1</p></div>');

		render(<App x={2} />, scratch);
		expect(spy2).to.be.calledThrice;
		expect(scratch.innerHTML).to.equal('<div><span>2</span><p>2</p></div>');
	});

	it('should not commit memoization from a skipped render', () => {
		const calls = [];
		let set;
		const App = () => {
			const [greeting, setGreeting] = useState('hi');
			set = setGreeting;

			const value = useMemo(() => {
				calls.push('doing memo');
				return greeting;
			}, [greeting]);
			calls.push(`render ${value}`);

			if (greeting === 'bye') {
				setGreeting('hi');
			}

			return <p>{value}</p>;
		};

		render(<App />, scratch);
		expect(calls.length).to.equal(2);
		expect(calls).to.deep.equal(['doing memo', 'render hi']);

		act(() => {
			set('bye');
		});
		expect(calls.length).to.equal(5);
		expect(calls).to.deep.equal([
			'doing memo',
			'render hi',
			'doing memo',
			'render bye', // We expect a missing "doing memo"  here because we return to the previous args value
			'render hi'
		]);
	});

	it('should promote falsy value after a skipped render', () => {
		let update;

		function App() {
			const [v, set] = useState(0);
			update = set;
			const res = useMemo(() => 0, [v > 1]);

			if (v === 0) {
				set(v + 1);
			}
			return <p>{res}</p>;
		}

		render(<App />, scratch);
		expect(scratch.textContent).to.equal('0');

		act(() => {
			update(v => v + 1);
		});
		act(() => {
			update(v => v + 1);
		});

		expect(scratch.textContent).to.equal('0');
	});

	it('should promote undefined value after a skipped render', () => {
		let value;
		function Comp({ all }) {
			const result = (value = useMemo(() => (all ? 5 : undefined), [all]));
			return result;
		}
		render(<Comp all />, scratch);
		expect(value).to.equal(5);
		render(<Comp all={false} />, scratch);
		expect(value).to.equal(undefined);
		render(<Comp all={false} />, scratch);
		expect(value).to.equal(undefined);
	});
});

import { setupRerender, act } from 'preact/test-utils';
import { createElement, render, createContext } from 'preact';
import { setupScratch, teardown } from '../../../test/_util/helpers';
import { useReducer, useEffect, useContext } from 'preact/hooks';

/** @jsx createElement */

describe('useReducer', () => {
	/** @type {HTMLDivElement} */
	let scratch;

	/** @type {() => void} */
	let rerender;

	beforeEach(() => {
		scratch = setupScratch();
		rerender = setupRerender();
	});

	afterEach(() => {
		teardown(scratch);
	});

	it('rerenders when dispatching an action', () => {
		const states = [];
		let _dispatch;

		const initState = { count: 0 };

		function reducer(state, action) {
			switch (action.type) {
				case 'increment':
					return { count: state.count + action.by };
			}
		}

		function Comp() {
			const [state, dispatch] = useReducer(reducer, initState);
			_dispatch = dispatch;
			states.push(state);
			return null;
		}

		render(<Comp />, scratch);

		_dispatch({ type: 'increment', by: 10 });
		rerender();

		expect(states).to.deep.equal([{ count: 0 }, { count: 10 }]);
	});

	it('can be dispatched by another component', () => {
		const initState = { count: 0 };

		function reducer(state, action) {
			switch (action.type) {
				case 'increment':
					return { count: state.count + action.by };
			}
		}

		function ReducerComponent() {
			const [state, dispatch] = useReducer(reducer, initState);
			return (
				<div>
					<p>Count: {state.count}</p>
					<DispatchComponent dispatch={dispatch} />
				</div>
			);
		}

		function DispatchComponent(props) {
			return (
				<button onClick={() => props.dispatch({ type: 'increment', by: 10 })}>
					Increment
				</button>
			);
		}

		render(<ReducerComponent />, scratch);
		expect(scratch.textContent).to.include('Count: 0');

		const button = scratch.querySelector('button');
		button.click();

		rerender();
		expect(scratch.textContent).to.include('Count: 10');
	});

	it('can lazily initialize its state with an action', () => {
		const states = [];
		let _dispatch;

		function init(initialCount) {
			return { count: initialCount };
		}

		function reducer(state, action) {
			switch (action.type) {
				case 'increment':
					return { count: state.count + action.by };
			}
		}

		function Comp({ initCount }) {
			const [state, dispatch] = useReducer(reducer, initCount, init);
			_dispatch = dispatch;
			states.push(state);
			return null;
		}

		render(<Comp initCount={10} />, scratch);

		_dispatch({ type: 'increment', by: 10 });
		rerender();

		expect(states).to.deep.equal([{ count: 10 }, { count: 20 }]);
	});

	it('provides a stable reference for dispatch', () => {
		const dispatches = [];
		let _dispatch;

		const initState = { count: 0 };

		function reducer(state, action) {
			switch (action.type) {
				case 'increment':
					return { count: state.count + action.by };
			}
		}

		function Comp() {
			const [, dispatch] = useReducer(reducer, initState);
			_dispatch = dispatch;
			dispatches.push(dispatch);
			return null;
		}

		render(<Comp />, scratch);

		_dispatch({ type: 'increment', by: 10 });
		rerender();

		expect(dispatches[0]).to.equal(dispatches[1]);
	});

	it('uses latest reducer', () => {
		const states = [];
		let _dispatch;

		const initState = { count: 0 };

		function Comp({ increment }) {
			const [state, dispatch] = useReducer(function (state, action) {
				switch (action.type) {
					case 'increment':
						return { count: state.count + increment };
				}
			}, initState);
			_dispatch = dispatch;
			states.push(state);
			return null;
		}

		render(<Comp increment={10} />, scratch);

		render(<Comp increment={20} />, scratch);

		_dispatch({ type: 'increment' });
		rerender();

		expect(states).to.deep.equal([{ count: 0 }, { count: 0 }, { count: 20 }]);
	});

	// Relates to #2549
	it('should not mutate the hookState', () => {
		const reducer = (state, action) => ({
			...state,
			innerMessage: action.payload
		});

		const ContextMessage = ({ context }) => {
			const [{ innerMessage }, dispatch] = useContext(context);
			useEffect(() => {
				dispatch({ payload: 'message' });
			}, []);

			return innerMessage && <p>{innerMessage}</p>;
		};

		const Wrapper = ({ children }) => <div>{children}</div>;

		const badContextDefault = {};
		const BadContext = createContext({});

		const Abstraction = ({ reducer, defaultState, children }) => (
			<BadContext.Provider value={useReducer(reducer, defaultState)}>
				<Wrapper>{children}</Wrapper>
			</BadContext.Provider>
		);

		const App = () => (
			<Abstraction reducer={reducer} defaultState={badContextDefault}>
				<ContextMessage context={BadContext} />
			</Abstraction>
		);

		act(() => {
			render(<App />, scratch);
		});
		expect(scratch.innerHTML).to.equal('<div><p>message</p></div>');
	});
});

